import numpy as np

TO_CHANGE = ['location', 'radius', 'max_radius', 'min_radius', 'focus1', 'focus2', 'x_radius', 'y_radius']
UNIT = {'m': 0.001, 'cm': 0.1}
def unitConverter(features: dict, unit: str) -> dict:
    if UNIT[unit]:
        scale = float(UNIT[unit])

        for key, value in features.items():                             # Dict
            for primitive in value:                                     # List
                for key2, value2 in primitive.items():                  # Dict
                    if key2 in TO_CHANGE:
                        if type(primitive[key2]) == list:
                            primitive[key2] = np.array(primitive[key2])
                            primitive[key2] *= scale
                            primitive[key2] = list(primitive[key2]) 
                        else:
                            primitive[key2] *= scale
    
    else:
        print('\nUnit not configured. Default: mm\n')


